import React from "react";
//random name generator
import namor from "namor";
//import "./index.css";

 const range = len => {
  const arr = [];
  for (let i = 1; i <= len; i++) {
    arr.push(i);
  }
  return arr;
};
 const MapDay = (week,day,data)=>{
  var i =0;
  for(i=0;i<data.length;i++){
    if ((data[i].week_number == week) && (data[i].day_number==day)){
      return {
        title:data[i].title,
        description:data[i].description
      }
    }
  }
  return {
    title:'',
    description:''
  }
}
const OneWeek=()=> {
  return {
      weekNo:0,
      Monday:{
        title:'',
        description:'',
      },
      Tuesday:{
        title:'',
        description:'',
      },
      Wednesday:{
        title:'',
        description:'',
      },
      Thursday:{
        title:'',
        description:'',
      },
      Friday:{
        title:'',
        description:'',
      },
      Saturday:{
        title:'',
        description:'',
      },
      Sunday:{
        title:'',
        description:'',
      },
  }
}
 const MappedWeek = (d,data)=>{
  const temp ={
      weekNo: d,
      Monday: {...MapDay(d,1,data)},
      Tuesday: {...MapDay(d,2,data)},
      Wednesday: {...MapDay(d,3,data)},
      Thursday: {...MapDay(d,4,data)},
      Friday: {...MapDay(d,5,data)},
      Saturday: {...MapDay(d,6,data)},
      Sunday: {...MapDay(d,7,data)},
  }
  console.log(temp);
  return temp;
}
export function getRequest(){ fetch('http://127.0.0.1:8000/planner/api/')
  .then(function(response) {
    return response.json();
  })
}

export function MapSemester(data, len =17){
    return range(len).map(d => {
      return {
        ...MappedWeek(d,data),
    };
  });
}
export function makeData(len = 17){
  return range(len).map(d => {
    return {
      ...MappedWeek(d,data),
  };
});
}
