import React from "react";
import { render } from "react-dom";
import { makeData, Logo, Tips } from "./Utils";

// Import React Table
import ReactTable from "react-table";
import "react-table/react-table.css";

class OneDay extends React.Component {
  constructor(props) {
  super(props);
  this.state = {
    value: null,
  };
}
render() {
  return (
    <div
      className="oneday">
      <p>Title: {this.props.value}</p>
      <p>Description: {this.props.value}</p>
    </div>


  );
}
}


class CalendarTable extends React.Component {
  constructor() {
    super();
    this.state = {
      data: makeData(),
    };
  }
  renderDay(daydata){
    return <OneDay value = {daydata} />
  }
  render() {
    const { data } = this.state;
    return (
      <div>
        <ReactTable

          data={data}
          columns={[{

      Header: 'Week',

      columns: [{
        Header: '',
        accessor: 'weekNo',
        style:{
          backgroundColor:'blue',
          color: 'white',
        },

        Cell: row=>(
          <span>
            {"Week "+row.value}
          </span>
        )
      }
      ]
    }, {
      Header: 'Day',
      columns: [{
        id:'Monday',
        Header: 'Mon',
        accessor: d=>d.Monday.title,
        headerStyle:{
          backgroundColor:'blue',
          color: 'white',
        },
        Cell: <OneDay/>
      }, {
        id:'Tuesday',
        Header: 'Tue',
        accessor: d=>d.Tuesday.title,
        headerStyle:{
          backgroundColor:'blue',
          color: 'white',
        },
        Cell: <OneDay />
      },{
        id:'Wednesday',
        Header: 'Wed',
        accessor: d=>d.Wednesday.title,
        headerStyle:{
          backgroundColor:'blue',
          color: 'white',
        },
        Cell: <OneDay />
      },{
        id:'Thursday',
        Header: 'Thu',
        accessor: d=>d.Thursday.title,
        headerStyle:{
          backgroundColor:'blue',
          color: 'white',
        },
        Cell: <OneDay />
      },{
        id:'Friday',
        Header: 'Fri',
        accessor: d=>d.Friday.title,
        headerStyle:{
          backgroundColor:'blue',
          color: 'white',
        },
        Cell: <OneDay/>
      },{
        id:'Saturday',
        Header: 'Sat',
        accessor: d=>d.Saturday.title,
        headerStyle:{
          backgroundColor:'blue',
          color: 'white',
        },
        Cell: <OneDay />
      },{
        id:'Sunday',
        Header: 'Sun',
        accessor: d=>d.Sunday.title,
        headerStyle:{
          backgroundColor:'blue',
          color:'white'
        },
        Cell: <OneDay />
      }]
    }]}
          defaultPageSize={17}
          className="-striped -highlight"   
          // Override table default
      sortable= {false} 
      resizable= {false} 
      filterable= {false} 
      showPagination= {false}
      showPaginationBottom= {false}
      showPageSizeOptions= {false}   
        />
        <br />
      </div>
    );
  }
}
export default CalendarTable;
