import React from "react";
import { render } from "react-dom";

import {Button,List,Popover} from 'antd';

const content = (
  <div>
    <p>This is a custom HTML popup</p>
  </div>
);

class OneMeeting extends React.Component {
  constructor(props) {
  super(props);
  this.state = {
    overview:{
      title: null,
      description:null,
    },
  };
}
//should put <popover> and <button> in title
render() {
    const data = this.props.overview;
    console.log('onemeeting:',data);
    return(
    <div>
      <Button>{data.title}</Button>
  </div>
    );
}
}

class AllMeeting extends React.Component {
    constructor(props) {
    super(props);
    this.state = {
      overview:[{
        title: null,
        description:null,
      }],
    };
  }
  //should put <popover> and <button> in title
  render() {
      const data = Object.values(this.props.overview);
      return(
      <div>
      <List
    itemLayout="horizontal"
    dataSource={data} 
    renderItem={item => (
      <List.Item>
        {content}
        <List.Item.Meta
          title={<OneMeeting overview={data}/>}
          description={item.description}
        />
      </List.Item>
    )}
  />
    </div>
      );
  }
  }

  export default AllMeeting;
