import React from "react";
//random name generator
import namor from "namor";
//import "./index.css";

const range = len => {
  const arr = [];
  for (let i = 1; i <= len; i++) {
    arr.push(i);
  }
  return arr;
};
const MapDay = (week,day,data)=>{
  var i =0;
  for(i=0;i<data.length;i++){
    if ((data[i].week_number == week) & (data[i].day_number==day)){
       return {
        title:data[i].title,
        description:data[i].description
      }
    }
  }
}
const OneRandomWeek = (week) => {
  return {
    weekNo: week,
    Monday: {
      title: Math.floor(Math.random() * 30),
      description: namor.generate({ words: 1, numbers: 0 }),
    },
    Tuesday: {
      title: Math.floor(Math.random() * 30),
      description: namor.generate({ words: 1, numbers: 0 }),
    },
    Wednesday: {
      title: Math.floor(Math.random() * 30),
      description: namor.generate({ words: 1, numbers: 0 }),
    },
    Thursday: {
      title: Math.floor(Math.random() * 30),
      description: namor.generate({ words: 1, numbers: 0 }),
    },
    Friday: {
      title: Math.floor(Math.random() * 30),
      description: namor.generate({ words: 1, numbers: 0 }),
    },
    Saturday: {
      title: Math.floor(Math.random() * 30),
      description: namor.generate({ words: 1, numbers: 0 }),
    },
    Sunday: {
      title: Math.floor(Math.random() * 30),
      description: namor.generate({ words: 1, numbers: 0 }),
    },
  };
};
const OneRandomDay = ()=>{
  return{
    title: Math.floor(Math.random() * 30),
      description: namor.generate({ words: 1, numbers: 0 }),
  }
}
const MappedWeek = (d,data)=>{
  return{
    weekNo: d,
      Monday: {...MapDay(d,2,data)},
      Tuesday: {...MapDay(d,2,data)},
      Wednesday: {...MapDay(d,3,data)},
      Thursday: {...MapDay(d,4,data)},
      Friday: {...MapDay(d,5,data)},
      Saturday: {...MapDay(d,6,data)},
      Sunday: {...MapDay(d,7,data)},
  };
}
const getRequest=()=> fetch('http://127.0.0.1:8000/planner/api/')
  .then(function(response) {
    return response.json();
  })
  .then(function(myJson) {
    console.log(JSON.stringify(myJson));

  });

export function makeData(len = 17) {
  const data =  getRequest();
  console.log(data);
  return range(len).map(d => {
    return {
      ...MappedWeek(d,data),
    };
  });
}

export const Logo = () =>
  <div style={{ margin: '1rem auto', display: 'flex', flexWrap: 'wrap', alignItems: 'center', justifyContent: 'center'}}>
    For more examples, visit {''}
  <br />
    <a href="https://github.com/react-tools/react-table" >
      <img
        src="https://github.com/react-tools/media/raw/master/logo-react-table.png"
        style={{ width: `150px`, margin: ".5em auto .3em" }}
      />
    </a>
  </div>;

export const Tips = () =>
  <div style={{ textAlign: "center" }}>
    <em>Tip: Hold shift when sorting to multi-sort!</em>
  </div>;
