import React from "react";
//random name generator
import namor from "namor";
//import "./index.css";

const range = len => {
  const arr = [];
  for (let i = 1; i <= len; i++) {
    arr.push(i);
  }
  return arr;
};
const OneDay = (data)=>{
  return{
    title:data.title,
    description:data.description,
  }
}
const OneWeek = (week) => {
  return {
    weekNo: week,
    Monday: Math.floor(Math.random() * 100),
    Tuesday: Math.floor(Math.random() * 100),
    Wednesday: Math.floor(Math.random() * 30),
    Thursday: Math.floor(Math.random() * 100),
    Friday: Math.floor(Math.random() * 100),
    Saturday: namor.generate({ words: 1, numbers: 0 }),
    Sunday: namor.generate({ words: 1, numbers: 0 })
  };
};

export function makeData(len = 17) {
  const fetchData = fetch('http://127.0.0.1:8000/planner/api/')
  const fetchJSON = fetchData.json();
  return range(len).map(d => {
    var tempday = {
    weekNo: d,
    Monday: {},
    Tuesday: {},
    Wednesday: {},
    Thursday: {},
    Friday: {},
    Saturday: {},
    Sunday: {}
    };

  });
}

export const Logo = () =>
