import React from "react";
import { render } from "react-dom";
import { makeData, Logo, Tips } from "./Utils";

// Import React Table
import ReactTable from "react-table";
import "react-table/react-table.css";



class OneDay extends React.Component {
  constructor(props) {
  super(props);
  this.state = {
    value: null,
  };
}
render() {
  return (
    <div
      className="oneday">
      {this.props.value}
    </div>

  );
}
}


class CalendarTable extends React.Component {
  constructor() {
    super();
    this.state = {
      data: makeData()
    };
  }
  renderDay(daydata){
    return <OneDay value = {daydata} />
  }
  render() {
    const { data } = this.state;
    return (
      <div>
        <ReactTable
          data={data}
          columns={[{
      Header: 'Week',

      columns: [{
        Header: '',
        accessor: 'weekNo',
        style:{
          backgroundColor:'blue',
          color: 'white',
        },

        Cell: row=>(
          <span>
            {"Week "+row.value}
          </span>
        )
      }
      ]
    }, {
      sortable: false, // use table default
    resizable: false, // use table default
    filterable: false, // use table default
      Header: 'Day',
      columns: [{

        Header: 'Mon',
        accessor: 'Monday',
        headerStyle:{
          backgroundColor:'blue',
          color: 'white',
        },
        Cell: <OneDay />
      }, {
        Header: 'Tue',
        accessor: 'Tuesday',
        headerStyle:{
          backgroundColor:'blue',
          color: 'white',
        },
        Cell: <OneDay />
      },{
        Header: 'Wed',
        accessor: 'Wednesday',
        headerStyle:{
          backgroundColor:'blue',
          color: 'white',
        },
        Cell: <OneDay />
      },{
        Header: 'Thu',
        accessor: 'Thursday',
        headerStyle:{
          backgroundColor:'blue',
          color: 'white',
        },
        Cell: <OneDay />
      },{
        Header: 'Fri',
        accessor: 'Friday',
        headerStyle:{
          backgroundColor:'blue',
          color: 'white',
        },
        Cell: <OneDay />
      },{
        Header: 'Sat',
        accessor: 'Saturday',
        headerStyle:{
          backgroundColor:'blue',
          color: 'white',
        },
        Cell: <OneDay />
      },{
        Header: 'Sun',
        accessor: 'Sunday',
        headerStyle:{
          backgroundColor:'blue',
          color:'white'
        },
        Cell: <OneDay />
      }]
    }]}
          defaultPageSize={17}
          className="-striped -highlight"
        />
        <br />
      </div>
    );
  }
}
export default CalendarTable;
