import React from "react";
import { render } from "react-dom";
import { MapSemester, makeData } from "./Utils";

// Import React Table
import ReactTable from "react-table";
import "react-table/react-table.css";

class OneDay extends React.Component {
  constructor(props) {
  super(props);
  this.state = {
    overview:{
      title: null,
      description:null,
    },
  };
}
render() {
  return (
    <div
      className="oneday">
      <p>Title: {this.props.overview.title}</p>
      <p>Description: {this.props.overview.description}</p>
    </div>
  );
}
}

function getRequest(){ fetch('http://127.0.0.1:8000/planner/api/')
  .then(function(response) {
    return response.json();
  })
}

class CalendarTable extends React.Component {
  constructor() {
    super();
    this.state = {
      data: makeData()
    };
  }
  componentDidMount(){
    getRequest().then((result) =>{
      console.log(result);
      var mappedresult = MapSemester(result);
      this.setState({data:mappedresult});
    });
  }

  render() {
    const { data } = this.state;
    return (
      <div>
        <ReactTable

          data={data}
          columns={[{

      Header: 'Week',

      columns: [{
        Header: '',
        accessor: 'weekNo',
        style:{
          backgroundColor:'blue',
          color: 'white',
        },

        Cell: row=>(
          <span>
            {"Week "+row.value}
          </span>
        )
      }
      ]
    }, {
      Header: 'Day',
      columns: [{
        id:'Monday',
        Header: 'Mon',
        accessor: d=>d.Monday.title,
        headerStyle:{
          backgroundColor:'blue',
          color: 'white',
        },
        Cell: row=>(
          <OneDay overview={row.original.Monday}/>
        )
      }, {
        id:'Tuesday',
        Header: 'Tue',
        accessor: d=>d.Tuesday.title,
        headerStyle:{
          backgroundColor:'blue',
          color: 'white',
        },
        Cell: row=>(
          <OneDay overview={row.original.Tuesday}/>
        )
      },{
        id:'Wednesday',
        Header: 'Wed',
        accessor: d=>d.Wednesday.title,
        headerStyle:{
          backgroundColor:'blue',
          color: 'white',
        },
        Cell: row=>(
          <OneDay overview={row.original.Wednesday}/>
        )
      },{
        id:'Thursday',
        Header: 'Thu',
        accessor: d=>d.Thursday.title,
        headerStyle:{
          backgroundColor:'blue',
          color: 'white',
        },
        Cell: row=>(
          <OneDay overview={row.original.Thursday}/>
        )
      },{
        id:'Friday',
        Header: 'Fri',
        accessor: d=>d.Friday.title,
        headerStyle:{
          backgroundColor:'blue',
          color: 'white',
        },
        Cell: row=>(
          <OneDay overview={row.original.Friday}/>
        )
      },{
        id:'Saturday',
        Header: 'Sat',
        accessor: d=>d.Saturday.title,
        headerStyle:{
          backgroundColor:'blue',
          color: 'white',
        },
        Cell: row=>(
          <OneDay overview={row.original.Saturday}/>
        )
      },{
        id:'Sunday',
        Header: 'Sun',
        accessor: d=>d.Sunday.title,
        headerStyle:{
          backgroundColor:'blue',
          color:'white'
        },
        Cell: row=>(
          <OneDay overview={row.original.Sunday}/>
        )
      }]
    }]}
          defaultPageSize={17}
          className="-striped -highlight"   
          // Override table default
      sortable= {false} 
      resizable= {false} 
      filterable= {false} 
      showPagination= {false}
      showPaginationBottom= {false}
      showPageSizeOptions= {false}   
        />
        <br />
      </div>
    );
  }
}
export default CalendarTable;
